package string;

import java.util.HashMap;
import java.util.Map;

public class Anagram {

    public static void main(String[] args) {
        
        String s1 = "";
        String s2 = "";
        
        System.out.println(areAnagrams(s1,s2));
    }
    public static boolean areAnagrams(String s1, String s2) {
        int si = s1.length();
        int sj = s2.length();

        Map<Character, Integer> m1 = new HashMap<>();
        Map<Character, Integer> m2 = new HashMap<>();

        if(si != sj){
            return false;
        }

        for(int i = 0; i < si; i++ ){

            if(m1.containsKey(s1.charAt(i))){
                m1.put(s1.charAt(i),m1.get(s1.charAt(i))+1);
            }else if(!m1.containsKey(s1.charAt(i))){
                m1.put(s1.charAt(i),1);
            }

            if(m2.containsKey(s2.charAt(i))){
                m2.put(s2.charAt(i),m2.get(s2.charAt(i))+1);
            }else if(!m2.containsKey(s2.charAt(i))){
                m2.put(s2.charAt(i),1);
            }

        }

        for(Map.Entry<Character,Integer> m : m1.entrySet()){
            char c = m.getKey();
            if(!m2.containsKey(c) || !m.getValue().equals(m2.get(c))){
                return false;
            }
        }

        return true;
    }

}
